import React from 'react'
import Row from './Row';

const Grid = (props) => {
    const {data} = props;
    const odd = data.length % 2;
    let i = 0;
    let video ;
    if(odd) {
        video = data[0];
        data = data.slice(1);
    }
    let array = []
    for(let i = 0;i < data.length;i = i + 2) {
        array.push({
            video1: data[i],
            video2: data[i+1],
            odd: i % 4 === 0
        });
    }
    return (
        <div>
            {
                array.map(pairs => (
                    <Row video1={pairs.video1} video2={pairs.video2} odd={pairs.odd} />
                ))
            }
            {
                odd ?
                <div> 
                    <Row video1={video} video2={undefined} />
                </div> 
                :
                <div></div>
            }
        </div>
    )
}


export default Grid
