import React, { useState, useEffect } from 'react'
import axios from "axios";
import Youtube from 'react-youtube';
import Loading from '../Loading';

const VideoPage = (props) => {

    const { id } = props.match.params;
    const [video, setVideo] = useState();
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            console.log(`https://localhost:5001/api/Video/${id}`);
            const response = await axios.get(`https://localhost:5001/api/Videos/${id}`);
            console.log(response);
            setVideo(response.data.video);
            setLoading(false);
            console.log(video);
        }
        fetchData();
    }, []);

    console.log(id);
    return (
        <div className="page">
            {
                loading ?
                    <Loading /> :
                    <Scrollbars autoHeight autoHeightMin={`calc(100vh - 120px)`}>

                        <Youtube
                            id={id}
                            videoId={video.youtubeLink.split('?v=')[1]}
                            className="w-100 mt-3"
                            containerClassName="yt-container mb-5"
                            opts={{
                                height: '720'
                            }}

                        />
                    </Scrollbars>
            }

        </div>
    )
}

export default VideoPage
