import React from 'react'
import Row from './Row';
import { useState } from 'react';

const Grid = (props) => {
    const {data} = props;
    const odd = data.length % 2;
    let i = 0;
    let video;
    function logic() {
        if(odd) {
            video = data[0];
            data.shift();
        }
        let array = []
        for(let i = 0;i < data.length;i = i + 2) {
            array.push({
                video1: data[i],
                video2: data[i+1],
                odd: i % 4 === 0
            });
        }
    }
    return (
        <div>
            {
                logic()
            }
            {
                
                odd ?
                <div> 
                    <Row video1={video} video2={undefined} />
                </div> 
                :
                <div></div>
            }
            {
                array.map(pairs => (
                    <Row video1={pairs.video1} video2={pairs.video2} odd={pairs.odd} />
                ))
            }
        </div>
    )
}


export default Grid
